// Function to clean HTML entities
function cleanTitle(title) {
    return title
        .replace(/&nbsp;/g, ' ')
        .replace(/&amp;/g, '&')
        .replace(/&lt;/g, '<')
        .replace(/&gt;/g, '>')
        .replace(/&quot;/g, '"')
        .replace(/<[^>]+>/g, '') // Remove any HTML tags
        .trim();
}

// Extract video URL and title pairs from HTML
function extractVideoTitles(html) {
    const videoMap = new Map();

    // Regex to find video src and following title
    const pattern = /<video[^>]*src="([^"]+)"[^>]*>[\s\S]*?<div class="font-semibold">(.*?)<\/div>/g;

    let match;
    while ((match = pattern.exec(html)) !== null) {
        const url = match[1];
        const title = cleanTitle(match[2]);

        if (url && title) {
            videoMap.set(url, title);
        }
    }

    return videoMap;
}

// Check if HTML is pasted
if (!htmlContent || htmlContent.trim() === '' || htmlContent.includes('PASTE_YOUR_HTML_HERE')) {
    console.log('\n❌ ERROR: No HTML content found!');
    console.log('\n📋 INSTRUCTIONS:');
    console.log('1. Open this file: update_video_titles.js');
    console.log('2. Find the line: const htmlContent = `');
    console.log('3. Replace PASTE_YOUR_HTML_HERE with your HTML');
    console.log('4. Save and run: node update_video_titles.js\n');
    process.exit(1);
}

console.log('\n🎬 VIDEO TITLE UPDATER');
console.log('======================\n');

// Extract titles from HTML
const videoTitles = extractVideoTitles(htmlContent);
console.log(`✅ Found ${videoTitles.size} videos with titles in HTML\n`);

// Read current videos.json
const videosPath = path.join(__dirname, 'data', 'videos.json');
let videos = [];

try {
    videos = JSON.parse(fs.readFileSync(videosPath, 'utf8'));
    console.log(`📂 Loaded ${videos.length} videos from videos.json\n`);
} catch (err) {
    console.error('❌ Error reading videos.json:', err.message);
    process.exit(1);
}

// Update titles
let updatedCount = 0;
let notFoundCount = 0;

videos.forEach((video, index) => {
    if (videoTitles.has(video.url)) {
        const newTitle = videoTitles.get(video.url);
        if (video.title !== newTitle) {
            console.log(`\n📝 Updating video ${index + 1}:`);
            console.log(`   OLD: ${video.title}`);
            console.log(`   NEW: ${newTitle}`);
            video.title = newTitle;
            updatedCount++;
        }
    } else {
        notFoundCount++;
        console.log(`\n⚠️  Video ${index + 1}: No title found in HTML`);
        console.log(`   URL: ${video.url.substring(0, 80)}...`);
    }
});

// Save updated videos.json
fs.writeFileSync(videosPath, JSON.stringify(videos, null, 2));

console.log('\n\n✅ UPDATE COMPLETE!');
